import express from 'express';
import 'express-async-errors';
import { errorHandlerMiddleware } from './middlewares';
import { corsMiddleware } from './middlewares/cors.middleware';
import { todosRoute } from './routes/todo.routes';

const api = express();

// req middlewares
api.use(express.json());
api.use(express.urlencoded({ extended: true }));
api.use(corsMiddleware);

// routes
api.use('/todos', todosRoute);
api.get('/', (_req, res) => {
  res.send({ message: 'teste' });
});

// res middlewares
api.use(errorHandlerMiddleware);

export default api;

import { ErrorRequestHandler } from 'express';

export const errorHandlerMiddleware: ErrorRequestHandler = (err, _req, res): void => {
  const { name, message } = err;
  switch (name) {
    case 'ValidationError':
      res.status(400).json({ message });
      break;
    default:
      res.sendStatus(500);
  }
};

import { RequestHandler } from 'express';

export const corsMiddleware: RequestHandler = (_req, res, next) => {
  res.setHeader('access-control-allow-headers', '*');
  res.setHeader('access-control-allow-methods', '*');
  res.setHeader('access-control-allow-origin', '*');
  next();
};

import { Router } from 'express';
import { todosController } from '../../app/controllers';

const todosRoute = Router();

// getTodo
todosRoute.get('/:id', async (req, res) => {
  res.send(req);
});

// editTodo
todosRoute.put('/:id', async (req, res) => {
  res.send(req);
});

// removeTodo
todosRoute.delete('/:id', async (req, res) => {
  res.send(req);
});

// listTodo
todosRoute.get('/', async (_req, res) => {
  const results = await todosController.list();
  return res.json(results);
});

// addTodo
todosRoute.post('/', async (req, res) => {
  const { body } = req;
  const results = await todosController.add(body);
  return res.status(201).json(results);
});


export { todosRoute };

import { Todo } from '../../types';
import { todosService } from '../services';
import { todosValidator } from '../validators';

export const todosController = {
  async list(): Promise<Todo[]> {
    const results = await todosService.list();
    return results;
  },

  async add(body: unknown): Promise<Todo | void> {
    const data = await todosValidator.bodyAdd(body);
    const id = await todosService.add(data);
    const results = await todosService.get(id);
    return results;
  }
};

import { AddTodo, Todo } from '../../types';
import { NotFoundError } from '../errors/not-found.error';
import { todosModel } from '../models';

export const todosService = {
  async list(): Promise<Todo[]> {
    const results = await todosModel.list();
    return results;
  },

  async add(data: AddTodo): Promise<Todo['id']> {
    const results = await todosModel.add(data);
    return results;
  },

  async get(id: Todo['id']): Promise<Todo | void> {
    const results = await todosModel.get(id);
    if (!results) throw new NotFoundError('todo not found');
    return results;
  }
};

export class NotFoundError extends Error {
  constructor(message?: string) {
    super(message);
    this.name = 'NotFoundError';
  }
}

import { todoDB } from '../../db';
import { AddTodo, Todo } from '../../types';

export const todosModel = {
  async list(): Promise<Todo[]> {
    const results = await todoDB.findAll({ raw: true });
    return results as unknown as Todo[];
  },

  async add(data: AddTodo): Promise<Todo['id']> {
    const results = await todoDB.create({ ...data, createdAt: new Date() }) as any;
    return results.id;
  },

  async get(id: Todo['id']): Promise<Todo> {
    const results = await todoDB.findOne({ where: { id }, raw: true });
    return results as unknown as Todo;
  }
};

import Joi from 'joi';
import { AddTodo } from '../../types';

export const todosValidator = {
  async bodyAdd(value: unknown): Promise<AddTodo> {
    const schema = Joi.object<AddTodo>({
      description: Joi.string(),
      isDone: Joi.boolean().default(false),
    });

    const result = await schema.validateAsync(value);
    return result;
  }
};
